" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pulmonary complications of sickle cell disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/28/27082/contributors\">",
