var title_f26_29_27101="Intestinal pathogen exposure I";
var content_f26_29_27101=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F75543%7EID%2F55625&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F75543%7EID%2F55625&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Exposures associated with specific intestinal pathogens",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Pathogen",
"       </td>",
"       <td class=\"subtitle1\">",
"        Epidemiologic clue(s) to",
"diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Bacteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Staphylococcus aureus",
"       </td>",
"       <td>",
"        Beef, pork, poultry, eggs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Clostridium perfringens",
"       </td>",
"       <td>",
"        Beef, pork, poultry, home-canned foods",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bacillus cereus",
"       </td>",
"       <td>",
"        Beef, pork, fried rice (Chinese), vegetables",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Enterhemorrhagic E. coli",
"       </td>",
"       <td>",
"        Beef, pork, fast food restaurants (undercooked",
"hamburger), apple cider, leaf lettuce, milk, cheese, extremes of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Enteroinvasive E. coli",
"       </td>",
"       <td>",
"        Milk, cheese",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Enterotoxigenic E. coli",
"       </td>",
"       <td>",
"        Travelers to developing world",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Salmonella",
"       </td>",
"       <td>",
"        Beef, pork, poultry, eggs, (eg, Caesar salad), raw",
"milk, ice cream, vegetables (eg, alfalfa sprouts), unpasteurized orange",
"juice, pet ducklings, lizards, rattlesnake meat",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Campylobacter",
"       </td>",
"       <td>",
"        Poultry (undercooked at barbecues), raw milk and cheeses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Shigella",
"       </td>",
"       <td>",
"        Daycare centers, vegetables (eg, green onions)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Yersinia",
"       </td>",
"       <td>",
"        Pork (not common), beef, milk, cheeses, hemochromatosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vibrio cholerae",
"       </td>",
"       <td>",
"        Shellfish from the Gulf of Mexico, inadequately cooked",
"seafood from South America, coconut milk from Thailand, airline",
"outbreaks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vibrio parahaemolyticus",
"       </td>",
"       <td>",
"        Ingestion of raw seafood, particularly in East Asia,",
"shellfish, cirrhosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Clostridium difficile",
"       </td>",
"       <td>",
"        Hospitalization, inpatient or outpatient antibiotic(s)",
"or chemotherapy within the last several weeks, daycare centers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Listeria",
"       </td>",
"       <td>",
"        Beef, pork, poultry, milk cheese, coleslaw, hot dogs,",
"potato salad, pregnancy, neonates, immunocompromised patients",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Exposures associated with specific intestinal pathogens",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Pathogen",
"       </td>",
"       <td class=\"subtitle1\">",
"        Epidemiologic clue(s) to",
"diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Viruses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rotavirus",
"       </td>",
"       <td>",
"        Daycare centers, nurseries, Australia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Noroviruses",
"       </td>",
"       <td>",
"        Schools, nursing homes, cruise ships, camps, military",
"barracks, vegetables, waterborne, foodborne, and shellfish-associated",
"outbreaks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hepatitis A",
"       </td>",
"       <td>",
"        Overcrowding, lack of clean water, patients and staff",
"of institutions, day care centers, men who have sex with men, IV drug",
"users, travelers, military barracks, shellfish (clams, oysters, mussels)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adenovirus",
"       </td>",
"       <td>",
"        Infantile diarrhea, ?AIDS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cytomegalovirus",
"       </td>",
"       <td>",
"        HIV-infected homosexual men with AIDS, organ",
"transplantation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Protozoa",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Giardia lamblia",
"       </td>",
"       <td>",
"        Daycare centers, swimming pools, travel (eg, St.",
"Petersburg, mountainous areas with ingestion of stream water), fruit",
"salad",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Entamoeba histolytica",
"       </td>",
"       <td>",
"        Travelers to endemic areas (eg, Mexico), for more than",
"one month, men who have sex with men, institutions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cryptosporidium",
"       </td>",
"       <td>",
"        Daycare centers, swimming pools, AIDS, farm animal",
"exposure, city water supply contamination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cyclospora",
"       </td>",
"       <td>",
"        Raspberries (from Guatemala)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Isospora",
"       </td>",
"       <td>",
"        Haiti, HIV infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Microsporidium",
"       </td>",
"       <td>",
"        AIDS (? travelers, ? fresh water)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
