" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Acute kidney injury (acute renal failure) in the newborn",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/30/27114/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?26/30/27114/contributors\">",
"     Tej K Mattoo, MD, DCH, FRCP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/30/27114/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
