var title_f26_30_27117="Digit reduction";
var content_f26_30_27117=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F72981&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F72981&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Digit dislocation reduction",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Preparation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Remove all rings from the affected hand or foot.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Assure no fractures or open joints are present on AP, true lateral, and oblique plain radiographs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        3. Assemble all necessary materials:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Latex free gloves",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1 percent buffered lidocaine without epinephrine (one part of 1 mEq/mL of sodium bicarbonate to 9 or 10 parts of 1 percent lidocaine)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Syringes (3 mL or 5 mL) and needles (25 or 27 gauge)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Povidone iodine solution (eg, Betadine&reg;) and alcohol",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Scissors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        White surgical tape",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Splinting materials depend on the affected joint:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3\">",
"        - Finger interphalangeal dislocations: padded, malleable, aluminum digital splint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3\">",
"        - Metacarpal (thumb) dislocations: water, bucket, prefabricated splinting material (eg, plaster of Paris or fiberglass)",
"        <strong>",
"         OR",
"        </strong>",
"        Webril&reg;, plaster of Paris, and stockinette to fashion a thumb spica splint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Provide appropriate analgesia as determined by the patient's age and degree of pain.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Interphalangeal reduction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Have an assistant brace the hand or foot.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Reduce the dislocation using one of the following methods:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         Dorsal dislocation:",
"        </strong>",
"        Provide longitudinal traction;",
"        <strong>",
"         gently",
"        </strong>",
"        hyperextend the joint while pushing the base of the dislocated phalanx into place.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         Volar dislocation:",
"        </strong>",
"        Provide longitudinal traction;",
"        <strong>",
"         gently",
"        </strong>",
"        hyperflex while pushing the base of the dislocated phalanx into place.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         Lateral dislocation:",
"        </strong>",
"        Provide longitudinal traction;",
"        <strong>",
"         gently",
"        </strong>",
"        hyperextend the joint while correcting the ulnar or radial deformity.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Test joint stability throughout full range of motion of the digit to ensure that the joint will recover well with splinting alone.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obtain a post-reduction plain radiograph, including a true lateral view of the digit.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Interphalangeal immobilization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Immobilize the digit based on the location and type of dislocation:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         DIP dorsal or lateral finger dislocation:",
"        </strong>",
"        Splint the DIP in full extension while allowing full range of motion of the PIP joint.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         PIP dorsal or lateral finger dislocation:",
"        </strong>",
"        Apply a dorsal splint with the PIP in 20 to 30 degrees of flexion.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         Volar finger dislocation:",
"        </strong>",
"        Splint the PIP and DIP in full extension.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         DIP or PIP toe dislocation:",
"        </strong>",
"        Buddy tape the affected digit to its neighbor.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Simple metacarpophalangeal dislocation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Reduction:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Have an assistant brace the hand and flex the wrist.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Avoid",
"        </strong>",
"        excessive hyperextension or distraction that can convert a simple dislocation into a complex dislocation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Gently",
"        </strong>",
"        distract the affected thumb or digit and apply volar pressure to the base of the dislocated proximal phalanx.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Test joint stability throughout the full range of thumb or finger motion to ensure that the joint will recover well with splinting alone.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Immobilization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Finger dislocation:",
"        </strong>",
"        Splint the digit in 90 degrees of flexion.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Thumb dislocation:",
"        </strong>",
"        Apply a thumb spica splint with the MCP joint in 20 degrees of flexion.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
