" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pulmonary artery catheterization: Indications and complications",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/31/27128/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?26/31/27128/contributors\">",
"     Gerald L Weinhouse, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
