var title_f26_31_27132="ACR fibromyalgia classification";
var content_f26_31_27132=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F79179&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F79179&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The American College of Rheumatology 1990 criteria for the classification of fibromyalgia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        1. History of widespread pain.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Definition.",
"        </strong>",
"        Pain is considered widespread when all of the following are present: pain in the left side of the body,",
"pain in the right side of the body, pain above the waist, and pain",
"below the waist. In addition, axial skeletal pain (cervical spine or",
"anterior chest or thoracic spine or low back) must be present. In this",
"definition, shoulder and buttock pain is considered as pain for each",
"involving side. \"Low back\" pain is considered lower segment pain.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        2. Pain in 11 of 18 tender point sites on digital palpation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Definition.",
"        </strong>",
"        Pain, on digital palpation, must be present in at least 11 of the following 18 tender point sites:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Occiput: bilateral, at the suboccipital muscle insertions.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Low cervical: bilateral, at the anterior aspects of the intertransverse spaces at C5-C7.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Trapezius: bilateral, at the midpoint of the upper border.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Supraspinatus: bilateral, at origins, above the scapula spine near the medial border.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Second rib: bilateral, at the second costochondral junctions, just lateral to the junctions on upper surfaces.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lateral epicondyle: bilateral, 2 cm distal to the epicondyles.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Gluteal: bilateral, in upper outer quadrants of buttocks in anterior fold of muscle.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Greater trochanter: bilateral, posterior to the trochanteric prominence.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Knee: bilateral, at the medial fat pad proximal to the joint line.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Digital palpation should be performed with an approximate",
"force of 4 kg. For tender point to be considered \"positive\" the subject",
"must state the palpation was painful. \"Tender\" is not to be considered",
"\"painful.\"",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * For classification purposes, patients will be said to have fibromyalgia if both criteria are satisfied. Widespread pain must have been present for at least three months. The presence of a second clinical disorder does not exclude the diagnosis of fibromyalgia.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Wolfe, F, Smythe, HA, Yunus, MB, et al. The American College of Rheumatology 1990 criteria for the classification of fibromyalgia. Report of the Multicenter Criteria Committee. Arthritis Rheum 1990; 33:160. Copyright &copy; 1990 John Wiley &amp; Sons.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f26_31_27132=[""].join("\n");
var outline_f26_31_27132=null;
var title_f26_31_27133="Spitz tumors histo differential";
var content_f26_31_27133=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F85847&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F85847&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histopathologic criteria for conventional Spitz nevi, atypical Spitz tumors, and melanoma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Conventional Spitz tumors",
"       </td>",
"       <td class=\"subtitle1\">",
"        Atypical Spitz tumors",
"       </td>",
"       <td class=\"subtitle1\">",
"        Melanoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Architecture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Diameter",
"       </td>",
"       <td>",
"        &lt;5 to 6 mm",
"       </td>",
"       <td>",
"        &ge;5 to 6 mm",
"       </td>",
"       <td>",
"        &ge;10 mm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Outline",
"       </td>",
"       <td>",
"        Symmetric (wedge-shaped)",
"       </td>",
"       <td>",
"        Asymmetric",
"       </td>",
"       <td>",
"        Asymmetric",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Circumscription",
"       </td>",
"       <td>",
"        Sharp lateral circumscription",
"       </td>",
"       <td>",
"        Often poor circumscription",
"       </td>",
"       <td>",
"        Poor circumscription",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Epidermal hyperplasia",
"       </td>",
"       <td>",
"        Present",
"       </td>",
"       <td>",
"        Effacement of the epidermis may be present",
"       </td>",
"       <td>",
"        Absent, or effacement of the epidermis may be present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Maturation with dermal depth",
"       </td>",
"       <td>",
"        Present",
"       </td>",
"       <td>",
"        Possibly diminished or lack of zonation and maturation",
"       </td>",
"       <td>",
"        Lack of zonation and maturation often present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Eosinophilic hyaline globules (Kamino bodies)",
"       </td>",
"       <td>",
"        Present",
"       </td>",
"       <td>",
"        Absent or few",
"       </td>",
"       <td>",
"        Typically absent",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        Cellular density",
"       </td>",
"       <td>",
"        Orderly nondisruptive infiltration of collagen by melanocytes",
"       </td>",
"       <td>",
"        High cellular density",
"       </td>",
"       <td>",
"        High cellular density",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Cytology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cellular population",
"       </td>",
"       <td>",
"        Uniform spindle and/or epithelioid",
"       </td>",
"       <td>",
"        Spindle and/or epithelioid cells showing increasing cytological atypia",
"       </td>",
"       <td>",
"        Spindle and/or epithelioid cells showing increasing cytological atypia and pleomorphism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cytoplasm",
"       </td>",
"       <td>",
"        Opaque or ground glass cytoplasm",
"       </td>",
"       <td>",
"        Granular versus ground glass cytoplasm",
"       </td>",
"       <td>",
"        Granular or mixed cytoplasm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nuclear to cytoplasmic ratio",
"       </td>",
"       <td>",
"        Low nuclear to cytoplasmic ratio",
"       </td>",
"       <td>",
"        Increasingly high nuclear to cytoplasmic ratio",
"       </td>",
"       <td>",
"        High nuclear to cytoplasmic ratio",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chromatin pattern",
"       </td>",
"       <td>",
"        Nuclei with open, delicate chromatin pattern",
"       </td>",
"       <td>",
"        Loss of delicate or dispersed chromatin pattern",
"       </td>",
"       <td>",
"        Loss of delicate or dispersed chromatin pattern",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nucleoli",
"       </td>",
"       <td>",
"        Uniform nucleoli",
"       </td>",
"       <td>",
"        Increasingly prominent nucleoli",
"       </td>",
"       <td>",
"        Large nucleoli",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pleomorphism",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        May be present",
"       </td>",
"       <td>",
"        Usually present",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        Hyperchromatism",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"       <td>",
"        May be present",
"       </td>",
"       <td>",
"        Present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Proliferation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mitotic rate",
"       </td>",
"       <td>",
"        &lt;2/mm",
"        <sup>",
"         2",
"        </sup>",
"        <br/>",
"        Absent or rare mitoses in deep dermis",
"        <br/>",
"        Lack of atypical mitoses",
"       </td>",
"       <td>",
"        <p>",
"         2 to 6/mm",
"         <sup>",
"          2",
"         </sup>",
"         (particularly &gt;6/mm",
"         <sup>",
"          2)",
"         </sup>",
"        </p>",
"        <p>",
"         Deep or marginal dermal mitoses may be present",
"        </p>",
"        <br/>",
"       </td>",
"       <td>",
"        <p>",
"         2 to 6/mm",
"         <sup>",
"          2",
"         </sup>",
"         (particularly &gt;6/mm",
"         <sup>",
"          2",
"         </sup>",
"         )",
"        </p>",
"        <p>",
"         Deep or marginal dermal mitoses frequently present",
"        </p>",
"        <br/>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Proliferative index (Ki-67 expression)",
"       </td>",
"       <td>",
"        &nbsp;&lt;2 percent",
"       </td>",
"       <td>",
"        <p>",
"         2 to &gt;10 percent",
"        </p>",
"       </td>",
"       <td>",
"        Often &gt;15 to 30 percent",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Barnhill RL. The Spitzoid lesion: rethinking Spitz tumors, atypical variants, 'Spitzoid melanoma' and risk assessment. Mod Pathol 2006; 19 Suppl 2:S21.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
