var content_f26_32_27148=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F83291&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F83291&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cosmeceuticals",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Cosmeceuticals",
"       </td>",
"       <td class=\"subtitle1\">",
"        Proposed mechanism of action",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Antioxidants",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Niacinamide (vitamin B3)",
"       </td>",
"       <td>",
"        Reduces transepidermal water loss, inhibits melanosome transfer, and increases collagen production",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ascorbic acid (Vitamin C)",
"       </td>",
"       <td>",
"        Protects from UVA-induced oxidative damage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Coenzyme Q10",
"       </td>",
"       <td>",
"        Protects from UVA-induced oxidative damage, suppresses the expression of UVA-induced collagenase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vitamin E",
"       </td>",
"       <td>",
"        Reduces UV induced erythema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Retinyl esters, retinol, retinaldehyde, oxoretinoids",
"       </td>",
"       <td>",
"        Reduce matrix metalloproteinases, promote collagen production",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Alpha-lipoic acid",
"       </td>",
"       <td>",
"        Acts as a scavenger of reactive oxygen species, prevents lipid peroxidation, has antiinflammatory properties, and acts as an exfoliant",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Hydroxyacids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Alpha hydroxy acids (glycolic acid, lactic acid, citric acid, malic acid)",
"        <br/>",
"        Beta hydroxy acids (salicylic acid)",
"       </td>",
"       <td>",
"        Reduce corneocyte adhesion, increase exfoliation, improve stratum corneum hydration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Plant extracts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Green tea polyphenolic epicatechins",
"       </td>",
"       <td>",
"        Inhibit UV-induced erythema, reduce the formation of cyclobutane pyrimidine dimers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Soy isoflavones (genistein and daidzein)",
"       </td>",
"       <td>",
"        Act as antioxidants and phytoestrogens, inhibit UV-stimulated production of matrix metalloproteinase and melanosome transfer to keratinocytes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Grape seeds (polyphenols)",
"       </td>",
"       <td>",
"        Have antioxidant and antiinflammatory properties, promote wound healing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Coffee berry (polyphenols, flavonoids, and flavonol glycosides)",
"       </td>",
"       <td>",
"        Neutralize free radicals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ginkgo biloba (polyphenols, flavonoids, and flavonol glycosides)",
"       </td>",
"       <td>",
"        Stimulate fibroblast proliferation and collagen production",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ginseng (ginsenosides)",
"       </td>",
"       <td>",
"        Has antioxidant and antiinflammatory properties",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
