var title_f26_32_27149="AASLD Rx chronic HBV recs";
var content_f26_32_27149=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F58520&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F58520&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for treatment of chronic hepatitis B",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        HBeAg",
"       </td>",
"       <td class=\"subtitle1\">",
"        HBV DNA (PCR)",
"       </td>",
"       <td class=\"subtitle1\">",
"        ALT",
"       </td>",
"       <td class=\"subtitle1\">",
"        Treatment strategy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        +",
"       </td>",
"       <td rowspan=\"4\">",
"        &gt;20,000 int. units/mL",
"       </td>",
"       <td rowspan=\"4\">",
"        &le;2 x ULN",
"       </td>",
"       <td>",
"        Low efficacy with current treatment.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Observe, consider treatment when ALT becomes elevated.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider biopsy in persons &gt;40 years, ALT persistently high normal-2x ULN, or with family history of HCC.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider treatment if HBV DNA &gt;20,000 int. units/mL and biopsy shows moderate/severe inflammation or significant fibrosis.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"12\">",
"        +",
"       </td>",
"       <td rowspan=\"12\">",
"        &gt;20,000 int. units/mL",
"       </td>",
"       <td rowspan=\"12\">",
"        &gt;2 x ULN",
"       </td>",
"       <td>",
"        Observe for 3 to 6 months and treat if no spontaneous HBeAg loss.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider liver biopsy prior to treatment if compensated.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immediate treatment if icteric or clinical decompensation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IFN&alpha;/pegIFN&alpha;, LAM, ADV, ETV, TDF or LdT may be used as initial therapy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ADV not preferred due to weak antiviral activity and high rate of resistance after first year.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        LAM and LdT not preferred due to high rate of drug resistance.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        End-point of treatment - Seroconversion from HBeAg to anti-HBe.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Duration of therapy:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; IFN-&alpha;: 16 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; PegIFN-&alpha;: 48 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; LAM/ADV/ETV/LdT/TDF: minimum&nbsp;one year, continue for at least&nbsp;six months after HBeAg seroconversion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IFN&alpha; nonresponders / contraindications to IFN&alpha;&rarr; TDF/ETV.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"8\">",
"        -",
"       </td>",
"       <td rowspan=\"8\">",
"        &gt;2000 int. units/mL",
"       </td>",
"       <td rowspan=\"8\">",
"        &gt;2 x ULN",
"       </td>",
"       <td>",
"        IFN-&alpha; / peg IFN-&alpha;, LAM, ADV, ETV, TDF or LdT may be used as initial therapy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        LAM and LdT not preferred due to high rate of drug resistance.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ADV not preferred due to weak antiviral activity and high risk of resistance after first year.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        End-point of treatment - not defined.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Duration of therapy:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; IFN-&alpha;/pegIFN-&alpha;:&nbsp;one year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; LAM/ADV/ETV/LdT/TDF: &gt;one year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IFN&alpha; nonresponders / contraindications to IFN-&alpha;&rarr; TDF/ETV.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        &gt;2000 int. units/mL",
"       </td>",
"       <td>",
"        1-&gt;2 x ULN",
"       </td>",
"       <td>",
"        Consider liver biopsy and treat if liver biopsy shows moderate/severe necroinflammation or significant fibrosis.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        &le;2000 int. units/mL",
"       </td>",
"       <td>",
"        &le;ULN",
"       </td>",
"       <td>",
"        Observe, treat if HBV DNA or ALT becomes higher.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"5\">",
"        +/-",
"       </td>",
"       <td rowspan=\"5\">",
"        Detectable",
"       </td>",
"       <td rowspan=\"5\">",
"        Cirrhosis",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Compensated:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; HBV DNA &gt;2000 int. units/mL - Treat, LAM/ADV/ETV/LdT/TDF may be used as initial therapy. LAM and LdT not preferred due to high rate of drug resistance; ADV not preferred due to weak antiviral activity and high risk of resistance after first year.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; HBV DNA &lt;2000 int. units/mL - Consider treatment if ALT elevated.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Decompensated:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Coordinate treatment with transplant center, LAM (or LdT) +ADV, TDF, or ETV preferred. Refer for liver transplant.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        +/-",
"       </td>",
"       <td rowspan=\"2\">",
"        Undetectable",
"       </td>",
"       <td rowspan=\"2\">",
"        Cirrhosis",
"       </td>",
"       <td>",
"        Compensated: Observe.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decompensated: Refer for liver transplant.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ALT: alanine aminotransferase; ULN: upper limit of normal; IFN&alpha;: interferon alpha; pegIFN-&alpha;: pegylated IFN-alpha; LAM: lamivudine; ADV: adefovir; ETV: entecavir; LdT: telbivudine; TDF: tenofovir disoproxil fumarate.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Lok ASF, McMahon BJ. Chronic hepatitis B: Update 2009. Hepatology 2009; 50:661. Available online at file://publish.aasld.org/Pages/Default.aspx. Accessed September 8th 2009. Copyright &copy; 2009 American Association for the Study of Liver Diseases.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
