var title_f26_33_27163="Kings College criteria";
var content_f26_33_27163=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F77717&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F77717&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    King's College Hospital criteria for liver transplantation in acute liver failure",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Acetaminophen-induced disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Arterial pH &lt;7.3 (irrespective of the grade of encephalopathy)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Grade III or IV encephalopathy AND",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Prothrombin time &gt;100 seconds AND",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Serum creatinine &gt;3.4mg/dL (301 &micro;mol/L)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       All other causes of acute liver failure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Prothrombin time &gt;100 seconds (irrespective of the grade of encephalopathy)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Any",
"       <strong>",
"        three",
"       </strong>",
"       of the following variables (irrespective of the grade of encephalopathy)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1. Age &lt;10 years or &gt;40 years",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Etiology: non-A, non-B hepatitis, halothane hepatitis, idiosyncratic drug reactions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Duration of jaundice before onset of encephalopathy &gt;7 days",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Prothrombin time &gt;50 seconds",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Serum bilirubin &gt;18 mg/dL (308 &micro;mol/L)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: O'Grady JG, Alexander GJM, Hayllar KM, et al. Gastroenterology 1989; 97:439.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
