var title_f26_33_27164="Eval isolated transaminitis";
var content_f26_33_27164=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F68475&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F68475&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of isolated mild chronic elevation of serum aminotransferases*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Step 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Review possible link to medications, herbal therapies, or recreational drugs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Screen for alcohol abuse (screening instruments, AST/ALT ratio &gt;2:1)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obtain serology for hepatitis B and C (HBsAg, anti-HBs, anti-HBc, anti-HCV)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Screen for hemochromatosis (Fe/TIBC &gt;45 percent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evaluate for fatty liver (AST/ALT usually &lt;1, obtain RUQ ultrasonography)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Step 2: If the above is unrevealing confirm that source is hepatic and consider proceeding to Step 3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Exclude muscle disorders (obtain creatinine kinase or aldolase)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obtain thyroid function tests (TSH if hypothyroidism is suspected otherwise obtain serum TSH, free T4, and T3 concentrations)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider celiac disease (especially in patients with a history of diarrhea or unexplained iron deficiency: serum antiendomysial IgA or anti tissue transglutaminase antibodies are reasonable screening tests)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider adrenal insufficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Step 3: Consider less common causes of liver disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider autoimmune hepatitis particularly in women and those with a history of other autoimmune disorders (check serum protein electrophoresis; obtain ANA and ASMA if positive)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider Wilson disease in those &lt;40 years age (check serum ceruloplasmin, evaluate for Kayser Fleischer rings)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider alpha-1 antitrypsin deficiency especially in patients with a history of emphysema out of proportion to their age or smoking history (obtain alpha-1 antitrypsin phenotype)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Step 4: Obtain a liver biopsy or observe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Observe if ALT and AST are less than twofold elevated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Otherwise consider a liver biopsy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ALT: alanine aminotransferase; ANA: antinuclear antibodies; ASMA: anti-smooth muscle antibodies; AST: aspartate aminotransferase; anti-HBs: antibody to hepatitis B surface antigen; anti-HBc: antibody to hepatitis B core antigen; anti-HCV: antibody to hepatitis C virus; HBsAg: hepatitis B surface antigen; Ig: immunoglobulin; RUQ: right upper quadrant; TIBC: total iron binding capacity; TSH: thyroid-stimulating hormone.",
"     <br/>",
"     * Mild is defined as &lt;250 unit/L; chronic is defined as more than six months.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
