var title_f26_34_27179="Kurtzke disability status scale";
var content_f26_34_27179=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F76465&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F76465&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Kurtzke disability status scale",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        1. No disability and minimal neurologic signs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Minimal disability (eg, slight weakness or stiffness, mild gait or visual disturbance)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Moderate disability (eg, monoparesis, mild hemiparesis, moderate ataxia, disturbing sensory loss, prominent urinary or eye symptom, or combination of lesser dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Relatively severe disability but fully ambulatory without aid, self sufficient and able to be up around 12 hours/day, does not prevent the ability to work or carry on normal living activities, excluding sexual dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5. Disability is severe enough to preclude working, maximal motor function involves walking unaided up to 500 meters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        6. Needs assistance with walking (eg, cane, crutches, or braces)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        7. Restricted to a wheelchair but able to wheel oneself and enter and leave chair without assistance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        8. Restricted to bed or chair, retains many self care functions and has effective use of arms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        9. Helpless and bedridden",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        10. Death due to multiple sclerosis (from respiratory paralysis, coma, following repeated or prolonged epileptic seizures)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
