var title_f26_37_27227="AIP HISORt criteria";
var content_f26_37_27227=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F74331&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F74331&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of criteria for diagnosing autoimmune pancreatitis (revised HISORt criteria)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <strong>",
"         <span class=\"red\">",
"          (H)",
"         </span>",
"         Histology",
"        </strong>",
"        suggestive of autoimmine pancreatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         <span class=\"red\">",
"          (I)",
"         </span>",
"        </strong>",
"        Pancreatic",
"        <strong>",
"         imaging",
"        </strong>",
"        suggestive of autoimmine pancreatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         <span class=\"red\">",
"          (S)",
"         </span>",
"         Serology",
"        </strong>",
"        (IgG4 &ge;2 times the upper limit of normal)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         <span class=\"red\">",
"          (O)",
"         </span>",
"        </strong>",
"        <strong>",
"         Other",
"        </strong>",
"        organ involvement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Biliary strictures, parotid/lacrimal gland involvement, mediastinal lymphadenopathy, retroperitoneal fibrosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         <span class=\"red\">",
"          (Rt)",
"         </span>",
"         &nbsp;Response to steroid treatment",
"        </strong>",
"        &nbsp;- Resolution/marked improvement of pancreatic and extrapancreatic manifestations",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Chari ST, Takahashi N, Levy MJ, et al. A diagnostic strategy to distinguish autoimmune pancreatitis from pancreatic cancer. Clin Gastroenterol Hepatol 2009; 7:1097.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
