" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Approach to chest pain in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/38/27241/contributors\">",
