var title_f26_42_27307="Physiologic Rx Malnutrition C";
var content_f26_42_27307=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F62304&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F62304&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Physiologic basis for treatment of severe malnutrition",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Affected organ or system",
"       </td>",
"       <td class=\"subtitle1\">",
"        Effects",
"       </td>",
"       <td class=\"subtitle1\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cellular function",
"       </td>",
"       <td>",
"        Sodium pump activity is reduced and cell membranes are more",
"permeable than normal, which leads to an increase in intracellular",
"sodium and a decrease in intracellular potassium and magnesium",
"        <br/>",
"        Protein synthesis is reduced",
"       </td>",
"       <td>",
"        Give large doses of potassium and magnesium to all children",
"        <br/>",
"        Restrict sodium intake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Skin, muscles and glands",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        The skin and subcutaneous fat are atrophied, which leads to loose folds of skin",
"        <br/>",
"        Many signs of dehydration are unreliable; eyes may be sunken because of loss of subcutaneous fat in the orbit",
"        <br/>",
"        Many glands, including the sweat, tear and salivary glands,",
"are atrophied; the child has dryness of the mouth and eyes and sweat",
"production is reduced",
"        <br/>",
"        Respiratory muscles are easily fatigued; the child is lacking in energy",
"       </td>",
"       <td>",
"        Rehydrate the child with ReSoMal or F-75 diet",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Management of Severe Malnutrition: A Manual for Physicians and other Senior Health Workers. World Health Organization, Geneva, 1999. Copyright &copy; 1999 World Health Organization.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
