"   <div class=\"block mop drugH1Div\" id=\"F225157\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Monitoring Parameters",
"    </span>",
"    <p style=\"text-indent:0em;display:inline\">",
"     Respiratory and cardiovascular status",
"    </p>",
"   </div>",
"   <div class=\"block rer drugH1Div\" id=\"F225161\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Reference Range",
"    </span>",
"    <p style=\"text-indent:0em;display:inline\">",
"     Therapeutic: 26 ng/mL after 24 hours",
"    </p>",
"   </div>",
"   <div class=\"list fbnlist drugH1Div drugBrandNames\" id=\"F225171\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     International Brand Names",
"    </span>",
"    <ul>",
