var title_f26_45_27355="Long term outcome in RA";
var content_f26_45_27355=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F82537&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F82537&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Natural history and prognosis of rheumatoid arthritis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        70 percent of joint erosions detectable by x-ray of hands and feet occur in the first two years of disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        At 20 years, over 60 percent of patients with RA belong to functional class III (significantly impaired, self-caring, using aids, requiring joint replacements or class IV (loss of independence requiring daily care)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Increased incidence of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Infections",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cardiovascular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Lymphomas",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Life-expectancy of RA less than age-sex matched control populations especially in patients with one or more of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Polyarticular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Systemic extra-articular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Persistent disease activity (high ESR, CRP)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Positive rheumatoid factor and circulating immune complex",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        HLA-DR &beta; chain \"shared epitope\", especially if bialleic",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
