var title_f26_45_27356="Empiric intravenous antibiotic therapy for animal bites";
var content_f26_45_27356=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F63280&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F63280&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Empiric intravenous antibiotic therapy for animal bites",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Adults",
"      </td>",
"      <td class=\"subtitle1\">",
"       Children",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Options for empiric gram-negative and anaerobic coverage include:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"2\">",
"       Monotherapy with a beta-lactam/beta-lactamase inhibitor, such as one of the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Ampicillin-sulbactam 3 g every six hours",
"      </td>",
"      <td>",
"       50 mg/kg per dose (based on ampicillin component) every six hours*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Piperacillin-tazobactam 4.5 g every eight hours",
"      </td>",
"      <td>",
"       125 mg/kg per dose (based on piperacillin component) every eight hours*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Ticarcillin-clavulanate 3.1 g every four hours",
"      </td>",
"      <td>",
"       50 mg/kg per dose (based on ticarcillin component) every four hours*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <p>",
"        A third generation cephalosporin such as ceftriaxone 1 g IV every 24 hours",
"       </p>",
"       <p>",
"        <strong>",
"         PLUS",
"        </strong>",
"       </p>",
"       <p>",
"        Metronidazole 500 mg IV every eight hours",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        A third generation cephalosporin such as ceftriaxone 100 mg/kg per dose every 24 hours*",
"       </p>",
"       <p>",
"        <strong>",
"         PLUS",
"        </strong>",
"       </p>",
"       <p>",
"        Metronidazole 10 mg/kg per dose every eight hours*",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Alternative empiric regimens include:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       A fluoroquinolone (eg, ciprofloxacin 400 mg IV every 12 hours or levofloxacin 500 mg IV daily)",
"       <strong>",
"        PLUS",
"       </strong>",
"       metronidazole 500 mg IV every eight hours",
"      </td>",
"      <td>",
"       Use fluoroquinolones with caution in children &lt;18 years of age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"2\">",
"       Monotherapy with a carbapenem&bull;, such as one of the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Imipenem-cilastatin 500 mg every six hours",
"      </td>",
"      <td>",
"       25 mg/kg per dose every six hours (maximum 500 mg per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Meropenem 1 g every eight hours",
"      </td>",
"      <td>",
"       20 mg/kg per dose every eight hours (maximum 1 g per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Ertapenem 1 g daily",
"      </td>",
"      <td>",
"       15 mg/kg per dose every 12 hours for children up to 12 years of age (maximum 500 mg per dose)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Maximum pediatric dose: See adult dose.",
"     <br>",
"      &bull; Carbapenems should not be used for patients with history of immediate-type hypersensitivity to beta-lactams.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
