"   <div class=\"block pri drugH1Div\" id=\"F218267\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Pregnancy Implications",
"    </span>",
"    <p style=\"text-indent:0em;display:inline\">",
"     Refer to Rifampin monograph.",
"    </p>",
"   </div>",
"   <div class=\"block lac drugH1Div\" id=\"F218275\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Lactation",
"    </span>",
"    <p style=\"text-indent:0em;display:inline\">",
"     Enters breast milk/compatible",
"    </p>",
"   </div>",
