var title_f26_46_27374="AED pharmacologic properties";
var content_f26_46_27374=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F60182&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F60182&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pharmacologic properties of antiepileptic drugs",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Metabolism and clearance",
"       </td>",
"       <td class=\"subtitle1\">",
"        Enzyme induction/inhibition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Protein binding, percent*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Half life in adults (hours)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Carbamazepine",
"       </td>",
"       <td>",
"        <p>",
"         &gt;90 percent metabolized by CYP 3A4 and 2C8 (minor) to active (epoxide) and inactive metabolites",
"        </p>",
"        <p>",
"         Dose adjustment is needed in severe renal or hepatic insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        Potent and broad spectrum inducer of CYP and P-gp",
"       </td>",
"       <td>",
"        75",
"       </td>",
"       <td>",
"        <p>",
"         25-65 (initial use, enzyme inducing naive patient)",
"        </p>",
"        <p>",
"         8-22 (after several weeks due to auto induction)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ethosuximide",
"       </td>",
"       <td>",
"        ~80 percent metabolized by CYP 3A4 and non-CYP transformations to inactive metabolites",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        &lt;5",
"       </td>",
"       <td>",
"        40-60",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Felbamate",
"       </td>",
"       <td>",
"        <p>",
"         60 percent metabolized by CYP 3A4 and 2E1 (minor); ~30 percent renally excreted as unchanged drug",
"        </p>",
"        <p>",
"         Dose adjustment is needed in renal insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Induces CYP 3A4 (moderate)",
"        </p>",
"        <p>",
"         Inhibits CYP 2C19 (minor)",
"        </p>",
"       </td>",
"       <td>",
"        25",
"       </td>",
"       <td>",
"        13-22 (prolonged in renal insufficiency)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gabapentin",
"       </td>",
"       <td>",
"        <p>",
"         &gt;95 percent renally excreted as unchanged drug (does not undergo metabolism)",
"        </p>",
"        <p>",
"         Dose adjustment is needed in renal insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        &lt;5",
"       </td>",
"       <td>",
"        5-7 (prolonged in renal insufficiency; &gt;130 hours in anuria)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lacosamide",
"       </td>",
"       <td>",
"        <p>",
"         &gt;40 percent renally excreted as unchanged drug; 30 percent metabolized by non-CYP transformations (including methylation) to inactive metabolite",
"        </p>",
"        <p>",
"         Dose adjustment is needed in renal insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        Inhibits 2C19 (minor)",
"       </td>",
"       <td>",
"        &lt;15",
"       </td>",
"       <td>",
"        13",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lamotrigine",
"       </td>",
"       <td>",
"        <p>",
"         &gt;90 percent metabolized by non-CYP transformations (including glucuronidation) to inactive metabolites",
"        </p>",
"        <p>",
"         Dose adjustment is needed in moderate to severe renal or hepatic insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        May induce its own metabolism by glucuronidation (minor)",
"       </td>",
"       <td>",
"        55",
"       </td>",
"       <td>",
"        12-62",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Levetiracetam",
"       </td>",
"       <td>",
"        <p>",
"         &gt;65 renally excreted as unchanged drug; 24 percent metabolized by non-CYP transformation (including hydrolysis) to inactive metabolites",
"        </p>",
"        <p>",
"         Dose adjustment is needed in renal insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        &lt;10",
"       </td>",
"       <td>",
"        6-8",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oxcarbazepine",
"       </td>",
"       <td>",
"        <p>",
"         Prodrug. &gt;90 percent metabolized by non-CYP transformations to pharmacologically active and inactive metabolites",
"        </p>",
"        <p>",
"         Dose adjustment is needed in severe renal insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        Induces CYP 3A4",
"        <span style=\"color: #ff0000;\">",
"        </span>",
"        (moderate to severe) and glucuronidation but does not induce its own metabolism",
"       </td>",
"       <td>",
"        40",
"       </td>",
"       <td>",
"        9 (active metabolite, prolonged in renal insufficiency)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perampanel&nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         &gt;90 percent metabolized by CYP 3A4, 3A5 and non-CYP transformations to inactive metabolites",
"        </p>",
"        <p>",
"         Dose adjustment is needed in mild or moderate hepatic insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        Appears to induce metabolism of progestin- containing hormonal contraceptives",
"       </td>",
"       <td>",
"        95&nbsp;",
"       </td>",
"       <td>",
"        105&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phenobarbital",
"       </td>",
"       <td>",
"        <p>",
"         75 percent metabolized by CYP 2C19, 2C9 (minor) and 2E1 (minor) to inactive metabolites; 25 percent excreted renally as unchanged drug",
"        </p>",
"        <p>",
"         Dose adjustment is needed in severe renal or hepatic insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        Potent and broad spectrum inducer of CYP and glucuronidation",
"       </td>",
"       <td>",
"        45",
"       </td>",
"       <td>",
"        75-110",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phenytoin",
"       </td>",
"       <td>",
"        <p>",
"         &gt;90 percent metabolized by CYP 2C9, 2C19 and 3A4 (minor) and non-CYP transformations to inactive metabolites; clearance is dose dependent, saturable, and may be subject to genetic polymorphism",
"        </p>",
"        <p>",
"         Dose adjustment is needed in severe renal or hepatic insufficiency; monitoring of free (unbound) concentrations also suggested",
"        </p>",
"       </td>",
"       <td>",
"        Potent and broad spectrum inducer of CYP and glucuronidation",
"       </td>",
"       <td>",
"        90-95",
"       </td>",
"       <td>",
"        9- &gt;42 (dose dependent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pregabalin",
"       </td>",
"       <td>",
"        <p>",
"         &gt;95 excreted renally as unchanged drug",
"        </p>",
"        <p>",
"         Dose adjustment is needed in renal insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        &lt;5",
"       </td>",
"       <td>",
"        6",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Primidone",
"       </td>",
"       <td>",
"        <p>",
"         75 percent metabolized by CYP 2C19, 2C9 (minor) and 2E1 (minor) to active intermediates; ~25 percent excreted renally as unchanged drug",
"        </p>",
"        <p>",
"         Dose adjustment is needed in moderate and severe renal or hepatic insufficiency; close monitoring of plasma levels suggested",
"        </p>",
"       </td>",
"       <td>",
"        Potent and broad spectrum inducer of CYP",
"       </td>",
"       <td>",
"        0-20",
"       </td>",
"       <td>",
"        <p>",
"         10-15 (parent)",
"        </p>",
"        <p>",
"         29-100 (active metabolite)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rufinamide",
"       </td>",
"       <td>",
"        &gt;90 percent metabolized by non-CYP transformations to inactive metabolites",
"       </td>",
"       <td>",
"        <p>",
"         Induces CYP 3A4 (minor)",
"        </p>",
"        <p>",
"         Inhibits CYP 2E1 (minor)",
"        </p>",
"       </td>",
"       <td>",
"        35",
"       </td>",
"       <td>",
"        6-10",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tiagabine",
"       </td>",
"       <td>",
"        &gt;90 percent metabolized by CYP 3A4 and non-CYP transformations to inactive metabolites",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        95",
"       </td>",
"       <td>",
"        <p>",
"         7-9",
"        </p>",
"        <p>",
"         2-5 (with enzyme-inducing AEDs)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Topiramate",
"       </td>",
"       <td>",
"        <p>",
"         &gt;65 percent excreted renally as unchanged drug; &lt;30 percent metabolized by non-CYP transformations to inactive metabolites; extent of metabolism is increased ~50 percent in patients receiving enzyme inducing AEDs",
"        </p>",
"        <p>",
"         Dose adjustment is needed in moderate and severe renal or hepatic insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Inhibits 2C19 (minor)",
"        </p>",
"        <p>",
"         Induces CYP 3A4 (minor)",
"        </p>",
"       </td>",
"       <td>",
"        9-17",
"       </td>",
"       <td>",
"        12-24",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Valproate",
"       </td>",
"       <td>",
"        <p>",
"         &gt;95 percent undergoes complex transformations including CYP 2C9, 2C19, 2A6, glucuronidation and non-CYP metabolism",
"        </p>",
"        <p>",
"         Dose adjustment is needed in hepatic insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Moderate broad spectrum inhibitor including CYP 2A6, 2B6, 2C9, 2C19, 2E1 and glucuronidation",
"        </p>",
"        <p>",
"         Minor or moderate inducer of CYP 2A6",
"        </p>",
"       </td>",
"       <td>",
"        80-95",
"       </td>",
"       <td>",
"        7-16",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vigabatrin",
"       </td>",
"       <td>",
"        <p>",
"         &gt;70 percent excreted renally as unchanged drug",
"        </p>",
"        <p>",
"         Dose adjustment is needed in renal insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        Induces CYP 2C9",
"       </td>",
"       <td>",
"        0&nbsp;",
"       </td>",
"       <td>",
"        5-13 (unrelated to duration of action)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Zonisamide",
"       </td>",
"       <td>",
"        <p>",
"         &gt;70 percent metabolized by CYP 3A4, 2C19 (minor) and non-CYP transformations",
"        </p>",
"        <p>",
"         Dose adjustment and/or slower titration is needed in mild renal insufficiency or hepatic insufficiency; not recommended in patients with moderate or severe renal insufficiency",
"        </p>",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        40",
"       </td>",
"       <td>",
"        63",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    AEDs: antiepileptic drugs; CYP: cytochrome P450; P-gp: membrane P-glycoprotein multidrug resistance transporter",
"    <div class=\"footnotes\">",
"     * Highly protein-bound AEDs exhibit altered pharmacokinetics, including greater therapeutic and toxic effects and drug interactions, when given in usual doses to patients with low serum albumin or protein binding affinity (eg, due to nephrotic syndrome or acidosis).&nbsp; Dose alteration is needed and monitoring of unbound (free) AED serum concentrations is suggested. See UpToDate topic for additional information.",
"    </div>",
"    <div class=\"reference\">",
"     Modified from:",
"     <br/>",
"     <ol>",
"      <li>",
"       Bazil CW. Antiepileptic drugs in the 21st century. CNS Spectr 2001; 6:756.",
"      </li>",
"      <li>",
"       Lacerda G, Krummel T, Sabourdy C, et al. Optimizing therapy of seizures in patients with renal or hepatic dysfunction. Neurology 2006; 67:S28.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
