" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Cardiac injury from blunt trauma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/47/27385/contributors\">",
