var title_f26_49_27420="Resuscitative measures for category II and III FHR tracings";
var content_f26_49_27420=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F57979&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F57979&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Various intrauterine resuscitative measures for Category II and/or III tracings",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Goal",
"       </td>",
"       <td class=\"subtitle1\">",
"        Associated FHR abnormality*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Potential intervention(s)",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        Promote fetal oxygenation and improve uteroplacental blood flow",
"       </td>",
"       <td rowspan=\"4\">",
"        <ul>",
"         <li>",
"          Recurrent late decelerations",
"         </li>",
"         <li>",
"          Prolonged decelerations or bradycardia",
"         </li>",
"         <li>",
"          Minimal or absent FHR variability",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        Place mother in a lateral position (either left or right)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer oxygen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Give IV fluid bolus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduce uterine contraction frequency",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Reduce uterine activity",
"       </td>",
"       <td rowspan=\"2\">",
"        <ul>",
"         <li>",
"          Tachysystole with Category II or III tracing",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        Discontinue oxytocin or cervical ripening agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer tocolytic medication (eg, terbutaline)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        Alleviate umbilical cord compression",
"       </td>",
"       <td rowspan=\"3\">",
"        <ul>",
"         <li>",
"          Recurrent variable decelerations",
"         </li>",
"         <li>",
"          Prolonged decelerations or bradycardia",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        Reposition mother (left or right lateral, hands and knees)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform amnioinfusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If umbilical cord prolapse is noted, elevate the presenting fetal part while preparing for operative delivery",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Evaluation for the underlying suspected cause(s) is also an important step in management of abnormal FHR tracings.",
"     <br/>",
"     &Delta; Depending on the suspected underlying cause(s) of FHR abnormality, combining multiple interventions simultaneously may be appropriate and potentially more effective than doing individually or serially.",
"     <sup>",
"      [1]",
"     </sup>",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br/>",
"     <ol>",
"      <li>",
"       Simpson KR, James DC. Efficacy of intrauterine resuscitation techniques in improving fetal oxygen status during labor. Obstet Gynecol 2005; 105:1362.",
"      </li>",
"     </ol>",
"     <br/>",
"     Modified and reproduced with permission from: ACOG Practice Bulletin #116. Management of intrapartum fetal heart rate tracings. Obstet Gynecol 2010; 116:1232. Copyright &copy; 2010 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
