var title_f26_50_27435="Diagnostic criteria Churg Strauss";
var content_f26_50_27435=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F76251&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F76251&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for eosinophilic granulomatosis with polyangiitis (Churg-Strauss)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        American College of Rheumatology criteria*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Lanham criteria",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Asthma (a history of wheezing or the finding of diffuse high-pitched wheezes on expiration)",
"       </td>",
"       <td>",
"        Asthma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eosinophilia &gt;10 percent on differential white blood cell count",
"       </td>",
"       <td>",
"        Eosinophilia &gt;1500 cells/microL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mononeuropathy (including multiplex) or polyneuropathy",
"       </td>",
"       <td>",
"        Systemic vasculitis involving two or more extra-pulmonary organs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Migratory or transient pulmonary opacities detected radiographically",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Paranasal sinus abnormality",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Biopsy containing a blood vessel showing the accumulation of eosinophils in extravascular areas",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Four of the six criteria needed and biopsy evidence of vasculitis.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     All three criteria needed.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
