" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pathology of bladder neoplasms",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/53/27481/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?26/53/27481/contributors\">",
"     Cristina Magi-Galluzzi, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?26/53/27481/contributors\">",
"     Ming Zhou, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/53/27481/contributors\">",
"     Section Editor",
"    </a>",
