var title_f26_53_27483="Administering positive pressure";
var content_f26_53_27483=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F76747&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F76747&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Advantages and disadvantages of the different methods for administering positive airway pressure",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Mode of positive pressure ventilation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        CPAP",
"       </td>",
"       <td>",
"        Inexpensive",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Lack of inspiratory pressure support",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Widely available",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Bi-level",
"       </td>",
"       <td>",
"        Widely available",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Tidal volume may may be limited by patient-related factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Can provide inspiratory pressure support to augment tidal volume",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Leak tolerant",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"4\">",
"        Volume-cycled",
"       </td>",
"       <td colspan=\"1\" rowspan=\"4\">",
"        Can set specific respiratory parameters",
"       </td>",
"       <td>",
"        More expensive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Less widely available",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Less well-tolerated than pressure support devices",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Leaks lead to loss of tidal volume",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
