" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of Lyme disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/54/27498/contributors\">",
