var title_f26_55_27516="Techniques of the initial phase of Interpersonal Psychotherapy";
var content_f26_55_27516=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F78732&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F78732&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Techniques of the initial phase of Interpersonal Psychotherapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Technique",
"       </td>",
"       <td class=\"subtitle1\">",
"        Strategy",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diagnose major depression",
"       </td>",
"       <td>",
"        Conduct psychiatric interview, including current and past psychiatric symptoms, prior treatment history, medical history, and family psychiatric history.",
"       </td>",
"       <td>",
"        Diagnosis is essential to prescribing appropriate treatment. This information is also used to select the IPT problem area.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Give the sick role",
"       </td>",
"       <td>",
"        Assign the patient a temporary social role that relieves him of his usual social obligations because of his medical condition (depression), while requiring him to focus on obligations associated with treatment.",
"       </td>",
"       <td>",
"        The concept of the sick role follows from the medical model in which depression is likened to other medical illness such as diabetes and asthma.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Take an interpersonal inventory",
"       </td>",
"       <td>",
"        Evaluate important interpersonal relationships and recent significant changes that have occurred in these relationships and in psychosocial functioning.",
"       </td>",
"       <td>",
"        The clinician focuses on the quality of current interpersonal relationships and recent changes in the patient's life.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Establish a depression timeline",
"       </td>",
"       <td>",
"        Establish the time of onset of current depressive episode (or exacerbation of depressive symptoms) and temporal relationship between changes in mood and changes in interpersonal functioning.",
"       </td>",
"       <td>",
"        Identifying the temporal relationship between current mood symptoms and onset of interpersonal problem will enable clinician to select a relevant treatment focus.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Select an interpersonal problem area and formulate the case",
"       </td>",
"       <td>",
"        Propose a treatment focus (interpersonal problem area) that links current mood symptoms to affectively meaningful change in life circumstances.",
"       </td>",
"       <td>",
"        Patient must agree to treatment focus; if there is disagreement, clinician revises the treatment formulation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide hope",
"       </td>",
"       <td>",
"        Maintain optimism.",
"       </td>",
"       <td>",
"        Providing hope is a nonspecific therapeutic factor that is common across different psychotherapies.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Discuss the treatment contract",
"       </td>",
"       <td>",
"        Negotiate treatment logistics including number of sessions, meeting times, fees, and procedures for managing issues that occur between sessions.",
"       </td>",
"       <td>",
"        The time-limited nature of acute IPT requires that treatment duration be specified from the outset.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
