var title_f26_56_27532="ACC AHA prediction periop risk";
var content_f26_56_27532=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F62292&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F62292&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Clinical predictors of increased perioperative cardiovascular risk (myocardial infarction, heart failure, death)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Major predictors that require intensive management and may lead to delay in or cancellation of the operative procedure unless emergent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Unstable coronary syndromes including unstable or severe angina or recent MI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Decompensated heart failure including NYHA functional class IV or worsening or new-onset HF",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Significant arrhythmias including high grade AV block, symptomatic ventricular arrhythmias, supraventricular arrhythmias with ventricular rate &gt;100 bpm at rest, symptomatic bradycardia, and newly recognized ventricular tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Severe heart valve disease including severe aortic stenosis or symptomatic mitral stenosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Other clinical predictors that warrant careful assessment of current status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; History of ischemic heart disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; History of cerebrovascular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; History of compensated heart failure or prior heart failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Diabetes mellitus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Renal insufficiency",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Fleisher LA, Beckman JA, Brown KA, Calkins H, Chaikof E, Fleischmann KE, Freeman WK, Froehlich JB, Kasper EK, Kersten JR, Riegel B, Robb JF, Smith SC Jr, Jacobs AK, Adams CD, Anderson JL, Antman EM, Buller CE, Creager MA, Ettinger SM, Faxon DP, Fuster V, Halperin JL, Hiratzka LF, Hunt SA, Lytle BW, Nishimura R, Ornato JP, Page RL, Riegel B, Tarkington LG, Yancy CW; American College of Cardiology; American Heart Association Task Force on Practice Guidelines (writing Committee to Revise the 2002 Guidelines on Perioperative Cardiovascular Evaluation for Noncardiac Surgery); American Society of Echocardiography; American Society of Nuclear Cardiology; Heart Rhythm Society; Society of Cardiovascular Anesthesiologists; Society for Cardiovascular Angiography and Interventions; Society for Vascular Medicine and Biology; Society for Vascular Surgery.",
"     <br>",
"      ACC/AHA 2007 guidelines on perioperative cardiovascular evaluation and care for noncardiac surgery: a report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing Committee to Revise the 2002 Guidelines on Perioperative Cardiovascular Evaluation for Noncardiac Surgery) developed in collaboration with the American Society of Echocardiography, American Society of Nuclear Cardiology, Heart Rhythm Society, Society of Cardiovascular Anesthesiologists, Society for Cardiovascular Angiography and Interventions, Society for Vascular Medicine and Biology, and Society for Vascular Surgery.",
"      <br>",
"       J Am Coll Cardiol. 2007 Oct 23;50(17):e159-241.",
"      </br>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
