" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Use of the signal-averaged electrocardiogram in ischemic heart disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/57/27544/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?26/57/27544/contributors\">",
"     Sanjiv M Narayan, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?26/57/27544/contributors\">",
"     Michael E Cain, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/57/27544/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?26/57/27544/contributors\">",
"     Ary L Goldberger, MD",
"    </a>",
"    <br/>",
"   </div>",
