" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of hypertension in children and adolescents",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
