var title_f26_62_27628="Spitz conventional histo";
var content_f26_62_27628=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F85843&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F85843&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histopathologic features of conventional Spitz nevi",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Architecture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diameter &lt;5 to 6 mm*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symmetric outline*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sharp lateral circumscription*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Regular pattern of epidermal hyperplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Maturation with dermal depth",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Zonation across the horizontal axis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wedge-shaped configuration within dermis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Orderly nondisruptive infiltration of collagen by melanocytes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cytology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Spindle or epithelioid cell type*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Uniform population of cells*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low nuclear to cytoplasmic ratio",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Opaque or ground glass cytoplasm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nuclei with open, delicate chromatin pattern",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Uniform nucleoli",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rare pleomorphism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Other helpful diagnostic features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mitotic rate &lt;2/mm",
"        <sup>",
"         2",
"        </sup>",
"        *",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Absent or rare mitoses in deep dermis*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lack of atypical mitoses*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Irregular contours of growth at deep margin*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Kamino bodies*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mononuclear or multinucleate giant cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minimal central pagetoid upward scatter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Junctional clefts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Loss of cohesion between cells (retraction spaces)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perivascular or diffuse inflammation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Superficial distribution of pigmentation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Telangiectasia and edema",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Most helpful features.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted by permission from: Macmillan Publishers Ltd: Modern Pathology. Barnhill RL. The Spitzoid lesion: Rethinking spitz tumors, atypical variants, 'Spitzoid melanoma' and risk assessment. Mod Pathol 2006; 19 Suppl 2:S21. Copyright &copy; 2006.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f26_62_27628=[""].join("\n");
