"     </li>",
"     <li>",
"      Spasmed (EE);",
"     </li>",
"     <li>",
"      Spasmex (AR, CN, IL, KP, RU, TH, TR, TW);",
"     </li>",
"     <li>",
"      Spasmo-Lyt (AE, BG, BH, CL, CY, CZ, DK, EG, FI, IL, IQ, IR, JO, KW, LB, LY, OM, QA, SA, SY, YE);",
"     </li>",
