var title_f26_9_26779="Secondary prophylaxis for rheumatic fever - Duration of therapy";
var content_f26_9_26779=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F55223&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F55223&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Secondary prophylaxis for rheumatic fever - Duration of therapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Category",
"      </td>",
"      <td class=\"subtitle1\">",
"       Duration after last attack",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <p>",
"        Rheumatic fever with carditis and residual heart disease",
"       </p>",
"       <p>",
"        (persistent valvular disease*)",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        10 years or until 40 years of age (whichever is longer);",
"       </p>",
"       <p>",
"        sometimes lifelong prophylaxis (see text)",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <p>",
"        Rheumatic fever with carditis but no residual heart disease",
"       </p>",
"       <p>",
"        (no valvular disease*)",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        10 years or until 21 years of age",
"       </p>",
"       <p>",
"        (whichever is longer)",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Rheumatic fever without carditis",
"      </td>",
"      <td>",
"       <p>",
"        5 years or until 21 years of age",
"       </p>",
"       <p>",
"        (whichever is longer)",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Clinical or echocardiographic evidence.",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: Gerber MA, Baltimore RS, Eaton CB, et al. Prevention of Rheumatic Fever and Diagnosis and Treatment of Acute Streptococcal Pharyngitis: A Scientific Statement From the American Heart Association Rheumatic Fever, Endocarditis, and Kawasaki Disease Committee of the Council on Cardiovascular Disease in the Young, the Interdisciplinary Council on Functional Genomics and Translational Biology, and the Interdisciplinary Council on Quality of Care and Outcomes Research. Circulation 2009; 119(11):1541-51. Copyright &copy; 2009 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
