var title_f27_13_27867="IBM versus polymyositis";
var content_f27_13_27867=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F74076&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F74076&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differentiation between inclusion body myositis and polymyositis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       &nbsp;",
"      </td>",
"      <td class=\"subtitle1\">",
"       Inclusion body myositis",
"      </td>",
"      <td class=\"subtitle1\">",
"       Polymyositis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Sex",
"      </td>",
"      <td>",
"       Male &gt; female",
"      </td>",
"      <td>",
"       Female &gt; male",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Age",
"      </td>",
"      <td>",
"       Rare before 50",
"      </td>",
"      <td>",
"       Common before 50",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Onset",
"      </td>",
"      <td>",
"       Insidious",
"      </td>",
"      <td>",
"       Acute or subacute",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Course",
"      </td>",
"      <td>",
"       Slowly progressive",
"      </td>",
"      <td>",
"       More rapid",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Distribution of weakness",
"      </td>",
"      <td>",
"       Variable, may be primarily distal",
"      </td>",
"      <td>",
"       Proximal, symmetric",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Creatine kinase",
"      </td>",
"      <td>",
"       Normal or &lt;10x normal",
"      </td>",
"      <td>",
"       Often &gt;10x normal",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       EMG",
"      </td>",
"      <td>",
"       Myopathic or mixed myopathic and neurogenic",
"      </td>",
"      <td>",
"       Myopathic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Muscle biopsy",
"      </td>",
"      <td>",
"       Inflammation, rimmed vacuoles, inclusions",
"      </td>",
"      <td>",
"       Inflammation, fiber necrosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Response to therapy",
"      </td>",
"      <td>",
"       Generally poor",
"      </td>",
"      <td>",
"       Expected",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
