var title_f27_14_27883="Platelet P2Y12 receptor blocker loading doses in acute STEMI";
var content_f27_14_27883=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F73079&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F73079&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Platelet P2Y12 receptor blocker loading doses in acute STEMI",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Reperfusion",
"       </td>",
"       <td class=\"subtitle1\">",
"        Type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Age (years)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Preferred drug (loading dose)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        Yes",
"       </td>",
"       <td rowspan=\"2\">",
"        PCI",
"       </td>",
"       <td rowspan=\"2\">",
"        ALL",
"       </td>",
"       <td>",
"        Ticagrelor loading dose 180 mg or prasugrel loading dose 60 mg, in patients without contraindications*. Both are preferred to clopidogrel.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clopidogrel loading dose 300-600 mg (600 mg is preferred), in patients for whom ticagrelor or prasugrel is not chosen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Fibrinolysis",
"       </td>",
"       <td>",
"        &lt;75",
"       </td>",
"       <td>",
"        Clopidogrel 300 mg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &ge;75",
"       </td>",
"       <td>",
"        Clopidogrel 75 mg",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        --",
"       </td>",
"       <td>",
"        ALL",
"       </td>",
"       <td>",
"        Ticagrelor 180 mg",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Absolute contraindications to prasugrel include a history of stroke or transient ischemic attack (T.I.A.) or active pathological bleeding. Weight &lt;60 kg and age &ge;75 years are relative contraindications.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
