var title_f27_15_27899="Contraindications perc liver bx";
var content_f27_15_27899=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F78374&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F78374&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Contraindications to a percutaneous liver biopsy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Absolute",
"       </td>",
"       <td class=\"subtitle1\">",
"        Relative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inability of patient to cooperate with procedure",
"       </td>",
"       <td>",
"        Morbid obesity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Significant coagulopathy or thrombocytopenia (unless corrected prior to the liver biopsy)",
"       </td>",
"       <td>",
"        Ascites",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hemophilia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        NSAID use (including aspirin) within last 7 to 10 days",
"       </td>",
"       <td>",
"        Infection within the right pleural cavity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patient refusal to accept blood transfusion or inability to provide blood transfusion support",
"       </td>",
"       <td>",
"        Infection below right hemidiaphragm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suspected hemangioma, vascular tumor, or echinococcal cyst",
"       </td>",
"       <td>",
"        Amyloidosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inability to identify an adequate biopsy site by percussion and/or ultrasound",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dilated biliary tree",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Bravo AA, Sheth SG, Chopra S. Liver biopsy. N Engl J Med 2001; 344:495.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
