var title_f27_15_27900="Acute stroke syndromes";
var content_f27_15_27900=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F75487&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F75487&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Acute stroke syndromes according to vascular territory and mechanism",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"25%\">",
"     </colgroup>",
"     <colgroup width=\"50%\">",
"     </colgroup>",
"     <colgroup width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Artery involved",
"       </td>",
"       <td class=\"subtitle1\">",
"        Syndrome",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pathophysiology",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Anterior cerebral artery",
"       </td>",
"       <td>",
"        <p>",
"         Motor and/or sensory deficit (leg &gt;&gt; face, arm)",
"        </p>",
"        <p>",
"         Grasp, sucking reflexes",
"        </p>",
"        Abulia, paratonic rigidity, gait apraxia",
"       </td>",
"       <td>",
"        Embolic &gt; atherothrombotic",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Middle cerebral artery",
"       </td>",
"       <td>",
"        <p>",
"         Dominant hemisphere: aphasia, motor and sensory deficit (face, arm &gt; leg &gt; foot), may be complete hemiplegia if internal capsule involved, homonymous hemianopia",
"        </p>",
"        Non-dominant hemisphere: neglect, anosognosia, motor and sensory deficit (face, arm &gt; leg &gt; foot), homonymous hemianopia",
"       </td>",
"       <td>",
"        Embolic &gt; atherothrombotic",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Posterior cerebral artery",
"       </td>",
"       <td>",
"        Homonymous hemianopia; alexia without agraphia (dominant hemisphere); visual hallucinations, visual perseverations (calcarine cortex); sensory loss, choreoathetosis, spontaneous pain (thalamus); III nerve palsy, paresis of vertical eye movement, motor deficit (cerebral peduncle, midbrain)",
"       </td>",
"       <td>",
"        Embolic &gt; atherothrombotic",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Penetrating vessels",
"       </td>",
"       <td>",
"        <p>",
"         Pure motor hemiparesis (classic lacunar syndromes)",
"        </p>",
"        <p>",
"         Pure sensory deficit",
"        </p>",
"        <p>",
"         Pure sensory-motor deficit",
"        </p>",
"        <p>",
"         Hemiparesis, homolateral ataxia",
"        </p>",
"        Dysarthria/clumsy hand",
"       </td>",
"       <td>",
"        Small artery (lacunar) infarct",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Vertebrobasilar",
"       </td>",
"       <td>",
"        <p>",
"         Cranial nerve palsies",
"        </p>",
"        <p>",
"         Crossed sensory deficits",
"        </p>",
"        <p>",
"         Diplopia, dizziness, nausea, vomiting, dysarthria, dysphagia, hiccup",
"        </p>",
"        <p>",
"         Limb and gait ataxia",
"        </p>",
"        <p>",
"         Motory deficit",
"        </p>",
"        <p>",
"         Coma",
"        </p>",
"        Bilateral signs suggest basilar artery disease",
"       </td>",
"       <td>",
"        Embolic = atherothrombotic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Internal carotid artery",
"       </td>",
"       <td>",
"        Progressive or stuttering onset of MCA syndrome, occasionally ACA syndrome as well if insufficient collateral flow",
"       </td>",
"       <td>",
"        Atherothrombotic &gt; embolic",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
