" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of subacute and chronic cough in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
