var title_f27_27_28093="Table of intrinsic muscles of the hand";
var content_f27_27_28093=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F66991&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F66991&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Intrinsic muscles of the hand",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Muscle",
"       </td>",
"       <td class=\"subtitle1\">",
"        Proximal attachment",
"       </td>",
"       <td class=\"subtitle1\">",
"        Distal attachment",
"       </td>",
"       <td class=\"subtitle1\">",
"        Innervation*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Main action",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Thenar muscles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Opponens pollicis",
"       </td>",
"       <td rowspan=\"5\">",
"        Flexor retinaculum and tubercles of scaphoid and trapezium",
"       </td>",
"       <td>",
"        Lateral side of 1st metacarpal",
"       </td>",
"       <td rowspan=\"4\">",
"        Recurrent branch of medial nerve (",
"        <strong>",
"         C8",
"        </strong>",
"        , T1)",
"       </td>",
"       <td>",
"        To oppose thumb, it draws 1st metacarpal medially to center of palm and rotates it medially",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Abductor pollicis brevis",
"       </td>",
"       <td rowspan=\"4\">",
"        Lateral side of base of proximal phalanx of thumb",
"       </td>",
"       <td>",
"        Abducts thumb; helps oppose it",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Flexor pollicis brevis",
"       </td>",
"       <td>",
"        Flexes thumb",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Superficial head",
"       </td>",
"       <td rowspan=\"5\">",
"        Adducts thumb toward lateral border of palm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Deep head",
"       </td>",
"       <td rowspan=\"4\">",
"        Deep branch of ulnar nerve (C8,",
"        <strong>",
"         T1",
"        </strong>",
"        )",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adductor pollicis",
"       </td>",
"       <td rowspan=\"2\">",
"        Bases of 2nd and 3rd metacarpals, capitate, adjacent carpals",
"       </td>",
"       <td rowspan=\"3\">",
"        Medial side of base of proximal phalanx of thumb",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Oblique head",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Transverse head",
"       </td>",
"       <td>",
"        Anterior surface of shaft of 3rd metacarpal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Hypothenar muscles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Abductor digiti minimi",
"       </td>",
"       <td>",
"        Pisiform",
"       </td>",
"       <td rowspan=\"2\">",
"        Medial side of base of proximal phalanx of 5th finger",
"       </td>",
"       <td rowspan=\"3\">",
"        Deep branch of ulnar nerve (C8,",
"        <strong>",
"         T1",
"        </strong>",
"        )",
"       </td>",
"       <td>",
"        Abducts 5th finger; assists in flexion of its proximal phalanx",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Flexor digiti minimi brevis",
"       </td>",
"       <td rowspan=\"2\">",
"        Hook of hamate and flexor retinaculum",
"       </td>",
"       <td>",
"        Flexes proximal phalanx of 5th finger",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Opponens digiti minimi",
"       </td>",
"       <td>",
"        Medial border of 5th metacarpal",
"       </td>",
"       <td>",
"        Draws 5th metacarpal anterior and rotates it, bringing 5th finger into opposition with thumb",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Short muscles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lumbricals",
"       </td>",
"       <td rowspan=\"2\">",
"        Lateral two tendons of flexor digitorum profundus (as unipennate muscles)",
"       </td>",
"       <td rowspan=\"3\">",
"        Lateral sides of extensor expansions of 2nd-5th fingers",
"       </td>",
"       <td rowspan=\"2\">",
"        Median nerve (C8,",
"        <strong>",
"         T1",
"        </strong>",
"        )",
"       </td>",
"       <td rowspan=\"3\">",
"        Flex metacarpophalangeal joints; extend interphalangeal joints of 2nd-5th fingers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        1st and 2nd",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        3rd and 4th",
"       </td>",
"       <td>",
"        Medial three tendons of flexor digitorum profundus (as bipennate muscles)",
"       </td>",
"       <td rowspan=\"3\">",
"        Deep branch of ulnar nerve (C8,",
"        <strong>",
"         T1",
"        </strong>",
"        )",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dorsal interossei, 1st-4th",
"       </td>",
"       <td>",
"        Adjacent sides of two metacarpals (as bipennate muscles)",
"       </td>",
"       <td>",
"        Bases of proximal phalanges; extensor expansions of 2nd-4th fingers",
"       </td>",
"       <td>",
"        Abduct 2nd-4th fingers from axial line; act with lumbricals in flexing metacarpophalangeal joints and extending interphalangeal joints",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Palmar interossei, 1st-3rd",
"       </td>",
"       <td>",
"        Palmar surfaces of 2nd, 4th, and 5th metacarpals (as unipennate muscles)",
"       </td>",
"       <td>",
"        Bases of proximal phalanges; extensor expansions of 2nd, 4th, and 5th fingers",
"       </td>",
"       <td>",
"        Adduct 2nd, 4th, and 5th fingers toward axial line; assist lumbricals in flexing metacarpophalangeal joints and extending interphalangeal joints; extensor expansions of 2nd-4th fingers",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * The spinal cord segmental innervation is indicated (eg, \"",
"     <strong>",
"      C8",
"     </strong>",
"     , T1\" means that the nerves supplying the opponens pollicis are derived from the eighth cervical segment and first thoracic segment of the spinal cord). Numbers in boldface (",
"     <strong>",
"      C8",
"     </strong>",
"     ) indicate the main segmental innervation. Damage to one or more of the listed spinal cord segments or to the motor nerve roots arising from them results in paralysis of the muscles concerned.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Moore KL, Dalley AR. Clinically Oriented Anatomy, 5th ed, Lippincott Williams &amp; Wilkins, Philadelphia 2006. Copyright &copy; 2006 Lippincott Williams &amp; Wilkins.",
"    </div>",
"    <div class=\"contractual\">",
"     <br/>",
"     <a href=\"file://www.lww.com\">",
"      file://www.lww.com",
"     </a>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
