" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pathology and pathogenesis of necrotizing enterocolitis in newborns",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/28/28105/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/28/28105/contributors\">",
"     Richard J Schanler, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/28/28105/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/28/28105/contributors\">",
"     Steven A Abrams, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/28/28105/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/28/28105/contributors\">",
"     Melanie S Kim, MD",
"    </a>",
"    <br/>",
"   </div>",
