var title_f27_29_28123="Postexposure rabies PI";
var content_f27_29_28123=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F65106&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F65106&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rabies postexposure guidelines",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Type of animal",
"      </td>",
"      <td class=\"subtitle1\">",
"       Observation or testing of animal",
"      </td>",
"      <td class=\"subtitle1\">",
"       Postexposure prophylaxis recommendations",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"3\">",
"       Dogs, cats, and ferrets",
"      </td>",
"      <td>",
"       Rabid or suspicion of rabies",
"      </td>",
"      <td colspan=\"1\">",
"       Immediately begin prophylaxis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"1\">",
"       Healthy and available for 10 days' observation",
"      </td>",
"      <td colspan=\"1\">",
"       <p>",
"        Do not begin prophylaxis unless:",
"       </p>",
"       <p>",
"        Bite or non-bite exposure is to the head and neck OR",
"       </p>",
"       <p>",
"        Animal develops signs of rabies",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Unavailable for observation or testing",
"      </td>",
"      <td colspan=\"1\">",
"       Consult public health officials; consider immediate vaccination",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Raccoons, foxes, skunks, coyotes and other carnivores; bats",
"      </td>",
"      <td>",
"       Consider rabid unless available for testing and proven not to be infected",
"      </td>",
"      <td colspan=\"1\">",
"       Consider immediate vaccination",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Large rodents, small rodents, livestock, lagomorphs and other mammals",
"      </td>",
"      <td>",
"       Consider on an individual basis",
"      </td>",
"      <td colspan=\"1\">",
"       Consult public health officials",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Non-mammals",
"      </td>",
"      <td>",
"       Do not harbor rabies",
"      </td>",
"      <td>",
"       Prophylaxis",
"       <strong>",
"        never",
"       </strong>",
"       required",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
