" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Drug-induced lupus",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/32/28168/contributors\">",
