" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Prevention and treatment of delirium and confusional states",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/33/28185/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/33/28185/contributors\">",
"     Joseph Francis, Jr, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/33/28185/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/33/28185/contributors\">",
"     Michael J Aminoff, MD, DSc",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/33/28185/contributors\">",
"     Kenneth E Schmader, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/33/28185/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/33/28185/contributors\">",
"     Janet L Wilterdink, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
