var title_f27_33_28187="Principles of prevention for exertional heat illness";
var content_f27_33_28187=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F83028&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F83028&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Principles of prevention for exertional heat illness",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Ensure that appropriate medical care and equipment, including a cold water immersion tub or other effective tools for rapid cooling, are available at practices and games.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Review and update emergency plans for exertional heat illness (EHI) annually with athletic directors, coaches, athletes, and medical personnel.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Educate the medical staff, emergency personnel, athletes, coaches, and parents about EHI and proper acclimatization and hydration strategies.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Include questions about EHI on pre-participation questionnaires and during examinations to identify \"high-risk\" individuals.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Establish and follow heat acclimatization guidelines: These should allow individuals to acclimatize over the course of 10-14 days, progressively increasing the intensity and duration of activity and the amount of equipment.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Establish and follow hot and humid weather activity guidelines that incorporate Wet Bulb Globe Temperature (WBGT) readings, time of day for activity, intensity and duration of activity, equipment, and rest and water breaks.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Establish and follow clear policies to ensure proper hydration: Use appropriate rest to work ratios, weigh-ins before and after activity, and encourage drinking both water and sodium-containing fluids during activity.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
