var title_f27_34_28204="Response to euthanasia requests";
var content_f27_34_28204=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F62176&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F62176&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Addressing patients' request for euthanasia or physician-assisted suicide",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Physician action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Specific interventions",
"       </td>",
"       <td class=\"subtitle1\">",
"        Rationale",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Inquire about the patient's interest in euthanasia or PAS",
"       </td>",
"       <td>",
"        \"What makes you interested in euthanasia or PAS?\"",
"       </td>",
"       <td>",
"        Allows you to understand what is important to the patient,",
"what he or she fears, and what is motivating his or her decisions.",
"Pain, suffering or dignity are ambiguous and \"catch all\" terms, explore",
"what is the underlying concern.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        \"Are things bad enough that you would want to end your life now?\"",
"       </td>",
"       <td>",
"        Allows you to understand whether the patient's inquiry is exploratory or a real request.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        \"Have you hoarded drugs or made other preparations to end your life?\"",
"       </td>",
"       <td>",
"        Allows you to understand the patient's seriousness and how they are thinking about ending their life.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Evaluate and treat physical and psychological symptoms",
"       </td>",
"       <td>",
"        Evaluate pain and pain control: \"Aside from everyday kinds of",
"pains like, toothaches, how much pain have you had during the past",
"week?\"",
"       </td>",
"       <td>",
"        While pain is not a major factor associated with interest in",
"or requests for euthanasia or PAS, it is an important symptom to treat.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evaluate other symptoms including dyspnea, incontinence, and fatigue",
"       </td>",
"       <td>",
"        While other symptoms have not been consistently associated",
"with interest in or requests for euthanasia or PAS, they are important",
"to treat.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Refer the patient to a psychiatrist, psychiatric social worker, psychologist, or counselor",
"       </td>",
"       <td>",
"        Depression and hopelessness are the leading factors",
"associated with interest in euthanasia or PAS and physicians tend to",
"under diagnose these conditions.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evaluate and treat care needs",
"       </td>",
"       <td>",
"        Determine how much assistance the patient needs and implement",
"home health care assistance where appropriate: \"How much help do you",
"need with bathing, dressing, or eating?\"",
"       </td>",
"       <td>",
"        Being a burden on family and care needs are factors associated with interest in euthanasia or PAS.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evaluate social relationships and supports",
"       </td>",
"       <td>",
"        Determine whether there is a person that the patient can talk",
"to and get emotional support from: \"Is there someone you can confied in",
"or talk about yourself or your problems?\" \"How often is there someone",
"to have a good time with?\"",
"       </td>",
"       <td>",
"        Feeling appreciated has been associated with less interest in euthanasia or PAS.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
