var title_f27_36_28236="Glenohumeral joint movements";
var content_f27_36_28236=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F70854&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F70854&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Glenohumeral joint movements",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Movement (function)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Prime mover(s) (from pendent position)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Synergists",
"       </td>",
"       <td class=\"subtitle1\">",
"        Notes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Flexion",
"       </td>",
"       <td>",
"        Pectoralis major (clavicular head); deltoid (anterior part)",
"       </td>",
"       <td>",
"        Coracobrachialis (assisted by biceps)",
"       </td>",
"       <td>",
"        From fully extended position to its own (coronal) plane, sternocostal head of pectoralis major is major force",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Extension",
"       </td>",
"       <td>",
"        Deltoid (posterior part)",
"       </td>",
"       <td>",
"        Teres major",
"       </td>",
"       <td>",
"        Latissimus dorsi, sternocostal head of pectoralis major, and long head of triceps act from fully flexed position to their own (coronal) planes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abduction",
"       </td>",
"       <td>",
"        Deltoid (as a whole, but especially central part)",
"       </td>",
"       <td>",
"        Supraspinatus",
"       </td>",
"       <td>",
"        Supraspinatus is particularly important in initiating movement; also, upward rotation of scapula occurs throughout movement, making a significant contribution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adduction",
"       </td>",
"       <td>",
"        Pectoralis major; latissimus dorsi",
"       </td>",
"       <td>",
"        Subscapularis; infraspinatus: teres minor",
"       </td>",
"       <td>",
"        In upright position and in absence of resistance, gravity is prime mover",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Internal&nbsp;rotation",
"       </td>",
"       <td>",
"        Subscapularis",
"       </td>",
"       <td>",
"        Pectoralis major; deltoid (anterior fibers); latissimus dorsi",
"       </td>",
"       <td rowspan=\"2\">",
"        With arm elevated, \"synergists\" become more important than prime movers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        External&nbsp;rotation",
"       </td>",
"       <td>",
"        Infraspinatus",
"       </td>",
"       <td>",
"        Teres minor; deltoid (posterior fibers)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tensors of articular capsule (to hold head of humerus against glenoid cavity)",
"       </td>",
"       <td>",
"        Supscapularis; infraspinatus (simultaneously)",
"       </td>",
"       <td>",
"        Supraspinatus; teres minor",
"       </td>",
"       <td>",
"        Rotator cuff (SITS) muscles acting together; when \"resting\", their tonus adequately maintains integrity of joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Resisting downward dislocation (shunt muscles)",
"       </td>",
"       <td>",
"        Deltoid (as a whole)",
"       </td>",
"       <td>",
"        Long head of triceps: coracobrachialis; short head of biceps",
"       </td>",
"       <td>",
"        Used especially when carrying heavy objects (suitcases, buckets)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Moore KL, Dalley AF. Clinically Oriented Anatomy, 5th Edition. Lippincott Williams &amp; Wilkins, Philadelphia 2006. Copyright &copy; 2006 Lippincott Williams &amp; Wilkins.",
"    </div>",
"    <div class=\"contractual\">",
"     <br/>",
"     <a href=\"file://www.lww.com\">",
"      file://www.lww.com",
"     </a>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f27_36_28236=[""].join("\n");
