" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Brugada syndrome",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/37/28250/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/37/28250/contributors\">",
"     John V Wylie, MD, FACC",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/37/28250/contributors\">",
"     Duane S Pinto, MD, MPH",
