var title_f27_39_28283="Eligibility criteria for auto-HCT";
var content_f27_39_28283=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F52191&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F52191&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    General eligibility criteria for autologous hematopoietic cell transplantation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Disease-related indication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Karnofsky performance status &ge;70",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Renal function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Serum creatinine &lt;2 mg/dL (177 micromol/L), unless autologous HCT is for myeloma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Cardiac reserve",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        LVEF &ge;40 percent if using the BEAM conditioning regimen and a LVEF &ge;50 percent using chemotherapy regimens with known cardiac toxicity (eg, cyclophosphamide)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Pulmonary function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        A corrected DLCO &ge;50 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Liver function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        No frank cirrhosis of the liver",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adequate psychosocial and financial support",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No evidence of myelodysplasia on bone marrow examination",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HCT: hematopoietic cell transplantation; LVEF: left ventricular ejection fraction; BEAM: BCNU, etoposide, cytarabine, and melphalan; DLCO: diffusing capacity of the lungs for carbon monoxide.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
