" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Epidemiology, prevention and control of vancomycin-resistant enterococci",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/4/27721/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/4/27721/contributors\">",
"     Deverick J Anderson, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/4/27721/contributors\">",
