var title_f27_41_28315="Resistant bacteria in LTCFs";
var content_f27_41_28315=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F70780&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F70780&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations of the Society for Hospital Epidemiology of America (SHEA) for controlling antibiotic resistant bacteria in long term care facilities",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Residents who are colonized with antimicrobial resistant",
"pathogens should not be denied entry into long term care facilities",
"(LTCFs).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decolonization therapy should not be required for residents",
"colonized with antimicrobial resistant pathogens prior to their",
"admittance to a LTCF.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        LTCF residents colonized with antimicrobial resistant",
"pathogens should not be restricted from participation in social or",
"therapeutic group activities within the facility unless there is reason",
"to think that they are shedding large numbers of bacteria and have been",
"implicated in the development of infections in other residents.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        LTCFs should obtain information on colonization or infection",
"with antimicrobial resistant pathogens of concern prior to receiving",
"new admissions.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Routine surveillance for selected antimicrobial resistant",
"pathogens should be performed in LTCFs and should include regular",
"review of all microbiologic data obtained in association with patient",
"care.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A line list of residents with antimicrobial resistant",
"pathogens should be maintained, differentiating colonization from",
"infection. Infection rates for resistant pathogens should be calculated.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Nicolle, LE, Bentley, D, Garibaldi, R, et al, Infect Control Hosp&nbsp;Epidemiol 1996;17:119.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
