var title_f27_41_28316="TOLAC recommendations of professional societies";
var content_f27_41_28316=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F74165&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F74165&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    TOLAC recommendations of professional societies",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Society",
"       </td>",
"       <td class=\"subtitle1\">",
"        VBAC counseling",
"       </td>",
"       <td class=\"subtitle1\">",
"        Facilities and personnel",
"       </td>",
"       <td class=\"subtitle1\">",
"        Other recommendations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The College",
"       </td>",
"       <td>",
"        VBAC should be offered to most women with one previous cesarean delivery with low transverse incision; consider those with two previous low transverse cesarean deliveries.",
"       </td>",
"       <td>",
"        Safest where staff can provide immediate emergency cesarean delivery, but patients should be allowed to accept increased risk when such resources are not available.",
"       </td>",
"       <td>",
"        Twins, macrosomia, postdatism, low vertical incision, and unknown type of uterine incision should not preclude.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        RCOG",
"       </td>",
"       <td>",
"        Women with one prior low segment cesarean delivery should be able to discuss option of VBAC; final decision between woman and her obstetrician.",
"       </td>",
"       <td>",
"        Should be conducted in suitably staffed and equipped delivery suite with continuous intrapartum care and monitoring and available resources for immediate cesarean delivery and advanced neonatal resuscitation.",
"       </td>",
"       <td>",
"        Caution with twins and macrosomia (uncertainty due to underpowered studies).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        SOGC",
"       </td>",
"       <td>",
"        VBAC should be offered to women with one previous cesarean delivery with low transverse incision.",
"       </td>",
"       <td>",
"        In hospital where a timely cesarean is available; an approximate timeframe of 30 min should be considered adequate for urgent laparotomy.",
"       </td>",
"       <td>",
"        Twins, macrosomia, and postdatism are not contraindications.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        AAFP",
"       </td>",
"       <td>",
"        VBAC should be offered to women with one previous cesarean delivery with low transverse incision.",
"       </td>",
"       <td>",
"        Should not be restricted only to those facilities with available surgical teams present throughout labor because there is no evidence that these additional resources result in improved outcome.",
"       </td>",
"       <td>",
"        Not addressed.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        AHRQ",
"       </td>",
"       <td>",
"        VBAC is a reasonable choice for the majority of women with prior cesarean delivery.",
"       </td>",
"       <td>",
"        Not addressed.",
"       </td>",
"       <td>",
"        Not addressed.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     VBAC: vaginal birth after cesarean delivery; The College: American College of Obstetricians and Gynecologists; RCOG: Royal College of Obstetricians and Gynecologists; SOGC: Society of Obstetricians and Gynecologists of Canada; AAFP: American Academy of Family Physicians; AHRQ: Agency for Healthcare Research and Quality.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Scott JR. Vaginal birth after cesarean delivery: a common-sense approach. Obstet Gynecol 2011; 118:342. Copyright &copy; 2011 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
